#!/usr/bin/env bash
# =============================================================================
# Theme: Kiribyte
# Variant: Dark
# Description: Soft pastel theme with purple-lavender accents
# =============================================================================

declare -gA THEME_COLORS=(
    # Status Bar
    [statusbar-bg]="#3b3f5c"
    [statusbar-fg]="#ffffff"

    # Session
    [session-bg]="#a9c98c"
    [session-fg]="#2a2b3d"
    [session-prefix-bg]="#e3caa6"
    [session-copy-bg]="#ade5ff"

    # Windows (base colors - variants auto-generated)
    [window-active-base]="#d4c5ff"
    [window-inactive-base]="#4d5270"

    # Pane Borders
    [pane-border-active]="#d4c5ff"
    [pane-border-inactive]="#4d5270"

    # Health States (base colors - variants auto-generated)
    [ok-base]="#687aa3"
    [good-base]="#a9c98c"
    [info-base]="#ade5ff"
    [warning-base]="#e3caa6"
    [error-base]="#ff6b85"
    [disabled-base]="#8a8fb5"

    # Messages
    [message-bg]="#3b3f5c"
    [message-fg]="#ffffff"

)
